import React, { Component, Fragment } from 'react';
// import $ from 'jquery';
// import 'magnific-popup';

class HeaderComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            navToggle: false,
            canvasmethod: false
        };
        this.toggleNav = this.toggleNav.bind(this);
        this.canvasToggle = this.canvasToggle.bind(this);
    }
    // Navigation
    toggleNav() {
        this.setState({
            navToggle: !this.state.navToggle
        });
    }
    // Canvas
    canvasToggle() {
        this.setState({
            canvasmethod: !this.state.canvasmethod
        });
    }
    // Sticky header
    componentDidMount() {
        // Popup
        // function popup() {
        //     $('.popup-video').magnificPopup({
        //         type: 'iframe'
        //     });
        // }
        // popup();
        window.addEventListener('scroll', () => {
            this.setState({
                isTop: window.scrollY > 100
            });
        }, false);
    }
    // Mobile menu
    getNextSibling = function (elem, selector) {
        // Get the next sibling element
        var sibling = elem.nextElementSibling;
        // If there's no selector, return the first sibling
        if (!selector) return sibling;
        // If the sibling matches our selector, use it
        // If not, jump to the next sibling and continue the loop
        while (sibling) {
            if (sibling.matches(selector)) return sibling;
            sibling = sibling.nextElementSibling
        }
    }

    triggerChild = (e) => {
        let subMenu = '';

        subMenu = (this.getNextSibling(e.target, '.submenu') !== undefined) ? this.getNextSibling(e.target, '.submenu') : null;

        if (subMenu !== null && subMenu !== undefined && subMenu !== '') {
            subMenu.classList = subMenu.classList.contains('d-block') ? 'submenu' : 'submenu d-block';
        }
    }
    render() {
        return (
            <Fragment />
        );
    }
}

export default HeaderComponent;
